import React from 'react';
import Button from '../img/button.png';

const BestPracticesTest = (props) => {
	const indent = props.indent;
	const toggle = Number(props.linkNumber);
	if (toggle === 2) {
		return (
			<div style={{ marginLeft: indent }}>
				<div style={style.Container}>
					<img src={Button} alt="-" />
					<p style={style.Text}>{props.text}</p>
				</div>
			</div>
		);
	}
	return (
		<div style={{ marginLeft: indent }}>
			<div style={style.Container}>
				<img src={Button} alt="-" />
				<p style={style.Text}>{props.text}</p>
			</div>
		</div>
	);
};

let style = {
	Container: {
		display: 'flex',
		alignItems: 'center',
		margin: '1rem 0',
	},
	Text: {
		fontSize: '1.2rem',
		fontWeight: '300',
		color: '#3B628B',
		margin: '0',
		paddingLeft: '1.5rem',
	},
};

export default BestPracticesTest;
