import React from 'react';
import { Link } from '@reach/router';
import LandingNavbar from './LandingNavbar';
import LandingCarousel from './LandingCarousel';
import Footer from './Footer';

import LogoWithSubtitle from '../img/logowithsubtitle.png';
import ConnectionRight from '../img/connection-right.svg';
import ConnectionLeft from '../img/connection-left.svg';

import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';

const Landing = () => {
	function setButtonHover(e) {
		e.target.style.color = 'white';
		e.target.style.background = '#7A98AF';
	}

	function unsetButtonHover(e) {
		e.target.style.color = '#7A98AF';
		e.target.style.background = 'transparent';
	}

	return (
		<div>
			<LandingNavbar />
			{/* <!-- Logo & Intro --> */}
			<section style={style.HomepageTop} className="text-center">
				<img
					src={LogoWithSubtitle}
					alt="Resource-19 Logo"
					style={style.HomepageLogo}
				/>
				<img
					src={ConnectionLeft}
					alt="connection left"
					style={style.HeaderImgLeft}
				/>
				<img
					src={ConnectionRight}
					alt="connection right"
					style={style.HeaderImgRight}
				/>
				<div className="text-center">
					<p style={style.HomepageIntro}>
						Want to donate or manufacture PPE for the COVID-19 crisis? Click
						<br />
						<b>Get Started</b> to find out how you can help, or{' '}
						<b>Go to the Makerspace</b>
						<br />
						to explore PPE designs.
					</p>
				</div>
				<Link to="/hospitals">
					<Button
						style={style.Button}
						onMouseEnter={setButtonHover}
						onMouseLeave={unsetButtonHover}
					>
						<b>Hospitals In Need</b>
					</Button>
				</Link>

				<Link to="/best-practices">
					<Button
						style={style.Button}
						onMouseEnter={setButtonHover}
						onMouseLeave={unsetButtonHover}
					>
						<b>Go to the Makerspace</b>
					</Button>
				</Link>
			</section>
			<LandingCarousel />
			<Container fluid>
				<iframe
					title="Hospital data"
					style={{ width: '110%', height: '90vh', border: 'none' }}
					id="hospitals-map"
					src="https://findthemasks.com/give.html"
				></iframe>
			</Container>

			<Footer />
		</div>
	);
};

let style = {
	HomepageTop: {
		paddingTop: '1vh',
		backgroundColor: 'white',
		textAlign: 'center',
		zIndex: '0',
		position: 'relative',
	},
	HomepageLogo: {
		width: '747px',
		padding: '0 5%',
		maxWidth: '80%',
		borderBottom: '1px solid #3b628b',
	},
	HeaderImgLeft: {
		position: 'absolute',
		zIndex: '-9',
		maxWidth: '266px',
		left: '2.5%',
		top: '-50px',
	},
	HeaderImgRight: {
		position: 'absolute',
		zIndex: '-9',
		maxWidth: '252px',
		right: '8%',
		top: '-60px',
	},
	HomepageIntro: {
		color: '#3b628b',
		fontSize: '28px',
		fontWeight: '400',
		padding: '25px 20% 0 20%',
	},
	Button: {
		border: '0px solid #3B628B',
		padding: '1.5rem 0',
		margin: '1rem',
		fontSize: '24px',
		minWidth: '30%',
		maxWidth: '50%',
		background: 'transparent',
		color: '#7A98AF',
		boxShadow: '0px 2px 6px #888888',
	},
};

export default Landing;
